import React, { useState, useReducer, useEffect } from "react";
import noteReducer from "../reducers/notes";
import NoteList from "./NoteList";
import AddNoteForm from "./AddNoteForm";

const AppNotes = () => {
  // const [notes, setNotes] = useState([]);
  const [notes, dispatch] = useReducer(noteReducer, []);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  const addNote = e => {
    e.preventDefault();
    // setNotes([...notes, { title, description }]);
    dispatch({
      type: "ADD_NOTE",
      title,
      description
    });
    setTitle("");
  };

  const removeNote = title => {
    // setNotes(notes.filter(note => note.title !== title));
    dispatch({
      type: "REMOVE_NOTE",
      title
    });
  };

  useEffect(() => {
    console.log("this should only be run once");
    const notesData = JSON.parse(localStorage.getItem("notez"));
    if (notesData) {
      dispatch({
        type: "POPULATE_NOTES",
        notes: notesData
      });
      // setNotes(notesData);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("notez", JSON.stringify(notes));
  }, [notes]);

  return (
    <div>
      <h1>notes</h1>
      <NoteList notes={notes} removeNote={removeNote} />
      <AddNoteForm />
    </div>
  );
};

export { AppNotes as default };
